import React, { useEffect, useState } from 'react';
import './App.css';

const HeroSection = () => {
  const [timeLeft, setTimeLeft] = useState('');

  useEffect(() => {
    const countdown = () => {
      const eventDate = new Date("February 1, 2025 00:00:00").getTime();
      const now = new Date().getTime();
      const distance = eventDate - now;

      const days = Math.floor(distance / (1000 * 60 * 60 * 24));
      const hours = Math.floor((distance % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
      const minutes = Math.floor((distance % (1000 * 60)) / (1000 * 60));
      const seconds = Math.floor((distance % (1000)) / 1000);

      if (distance < 0) {
        setTimeLeft('EXPIRED');
        return;
      }

      setTimeLeft(`${days}d ${hours}h ${minutes}m ${seconds}s`);
    };

    const timer = setInterval(countdown, 1000);
    return () => clearInterval(timer);
  }, []);

  return (
    <section id="home" className="hero-section">
      <div className="video-section">
        <video autoPlay loop muted playsInline>
          <source src="bg.mp4" type="video/mp4" />
          Your browser does not support the video tag.
        </video>
        <div className="hero-content">
          <h1>MOSAIC CULTURAL FEST</h1>
          <p id="dates">30th January, 31st January & 1st February 2025</p>
          <div id="countdown-timer">{timeLeft}</div>
          <a href="event.html" className="btn">Register Now</a>
          <p id="scroll">Scroll for events</p>
        </div>
      </div>
    </section>
  );
};

export default HeroSection;
