import React, { useState, useEffect } from 'react';
import './App.css';

const HeroSection = () => {
  const [timeLeft, setTimeLeft] = useState({
    days: 0,
    hours: 0,
    minutes: 0,
    seconds: 0,
  });

  const [currentIndex, setCurrentIndex] = useState(0); // Track the current card index

  const calculateTimeLeft = () => {
    const eventDate = new Date("February 1, 2025 00:00:00").getTime();
    const now = new Date().getTime();
    const distance = eventDate - now;

    if (distance <= 0) {
      return null; // Event expired
    }

    const days = Math.floor(distance / (1000 * 60 * 60 * 24));
    const hours = Math.floor((distance % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
    const minutes = Math.floor((distance % (1000 * 60 * 60)) / (1000 * 60));
    const seconds = Math.floor((distance % (1000 * 60)) / 1000);

    return { days, hours, minutes, seconds };
  };

  useEffect(() => {
    const interval = setInterval(() => {
      const time = calculateTimeLeft();
      if (time) {
        setTimeLeft(time);
      } else {
        clearInterval(interval); // Clear interval if the event is over
        setTimeLeft(null); // Set to null to indicate expiration
      }
    }, 1000);

    return () => clearInterval(interval); // Cleanup interval on unmount
  }, []);

  useEffect(() => {
    const handleKeyDown = (e) => {
      if (e.key === 'ArrowRight' && currentIndex < 2) { // Assuming 3 cards
        setCurrentIndex(prevIndex => prevIndex + 1);
      } else if (e.key === 'ArrowLeft' && currentIndex > 0) {
        setCurrentIndex(prevIndex => prevIndex - 1);
      }
    };

    document.addEventListener('keydown', handleKeyDown);

    return () => {
      document.removeEventListener('keydown', handleKeyDown);
    };
  }, [currentIndex]);

  return (
    <div>
      {/* Hero Section */}
      <section id="home" className="hero-section">
        <div className="image-section">
          <div className="hero-content">
            <h1>MOSAIC CULTURAL FEST</h1>
            <h2 id="dates">30th January, 31st January & 1st February 2025</h2>
            <div id="countdown-timer">
              {timeLeft ? (
                <>
                  {timeLeft.days}d {timeLeft.hours}h {timeLeft.minutes}m {timeLeft.seconds}s
                </>
              ) : (
                "EXPIRED"
              )}
            </div>
            <a href="#events" className="btn">Register</a>
          </div>
        </div>
      </section>

      {/* Events Section */}
      <section id="events" className="events-section">
        <h2>Event Details</h2>
        <div className="events-slider">
          <div className="events-container" style={{ transform: `translateX(-${currentIndex * 33.33}%)` }}>
            {Array.from({ length: 3 }).map((_, index) => (
              <div className="event" key={index}>
                <h3>Event {index + 1}</h3>
                <p>Date: 30th January 2025</p>
                <p>Time: 6:00 PM - 9:00 PM</p>
                <p>Venue: Main Auditorium</p>
                <button className="btn1">Register Now</button>
              </div>
            ))}
          </div>
        </div>
      </section>
    </div>
  );
};

export default HeroSection;
