import React, { useState, useEffect } from 'react';
import './App.css';

const HeroSection = () => {
  const [timeLeft, setTimeLeft] = useState({
    days: 0,
    hours: 0,
    minutes: 0,
    seconds: 0,
  });

  const calculateTimeLeft = () => {
    const eventDate = new Date("February 1, 2025 00:00:00").getTime();
    const now = new Date().getTime();
    const distance = eventDate - now;

    if (distance <= 0) {
      return null; // Event expired
    }

    const days = Math.floor(distance / (1000 * 60 * 60 * 24));
    const hours = Math.floor((distance % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
    const minutes = Math.floor((distance % (1000 * 60 * 60)) / (1000 * 60));
    const seconds = Math.floor((distance % (1000 * 60)) / 1000);

    return { days, hours, minutes, seconds };
  };

  useEffect(() => {
    const interval = setInterval(() => {
      const time = calculateTimeLeft();
      if (time) {
        setTimeLeft(time);
      } else {
        clearInterval(interval); // Clear interval if the event is over
        setTimeLeft(null); // Set to null to indicate expiration
      }
    }, 1000);

    return () => clearInterval(interval); // Cleanup interval on unmount
  }, []);

  return (
    <section id="home" className="hero-section">
      <div className="image-section">
        <div className="hero-content">
          <h1>MOSAIC CULTURAL FEST</h1>
          <p id="dates"><h2>30th January, 31st January & 1st February 2025</h2></p>
          <div id="countdown-timer">
            {timeLeft ? (
              <>
                {timeLeft.days}d {timeLeft.hours}h {timeLeft.minutes}m {timeLeft.seconds}s
              </>
            ) : (
              "EXPIRED"
            )}
          </div>
          <a href="event.html" className="btn">Register Now</a>
          <p id="scroll">Scroll for events</p>
        </div>
      </div>
      <h2>Event Details</h2>
        <div class="event">
            <h3>Music Concert</h3>
            <p>Date: 30th January 2025</p>
            <p>Time: 6:00 PM - 9:00 PM</p>
            <p>Venue: Main Auditorium</p>
            <button class="btn1">Register Now</button>
        </div>
        <div class="event">
            <h3>Dance Competition</h3>
            <p>Date: 31st January 2025</p>
            <p>Time: 4:00 PM - 7:00 PM</p>
            <p>Venue: Open Stage</p>
            <button class="btn1">Register Now</button>
        </div>
        <div class="event">
            <h3>Art Exhibition</h3>
            <p>Date: 1st February 2025</p>
            <p>Time: 10:00 AM - 5:00 PM</p>
            <p>Venue: Art Gallery</p>
            <button class="btn1">Register Now</button>
        </div>
        <div class="event">
            <h3>Music Concert</h3>
            <p>Date: 30th January 2025</p>
            <p>Time: 6:00 PM - 9:00 PM</p>
            <p>Venue: Main Auditorium</p>
            <button class="btn1">Register Now</button>
        </div>
        <div class="event">
            <h3>Dance Competition</h3>
            <p>Date: 31st January 2025</p>
            <p>Time: 4:00 PM - 7:00 PM</p>
            <p>Venue: Open Stage</p>
            <button class="btn1">Register Now</button>
        </div>
        <div class="event">
            <h3>Art Exhibition</h3>
            <p>Date: 1st February 2025</p>
            <p>Time: 10:00 AM - 5:00 PM</p>
            <p>Venue: Art Gallery</p>
            <button class="btn1">Register Now</button>
        </div>
        <div class="event">
            <h3>Music Concert</h3>
            <p>Date: 30th January 2025</p>
            <p>Time: 6:00 PM - 9:00 PM</p>
            <p>Venue: Main Auditorium</p>
            <button class="btn1">Register Now</button>
        </div>
        <div class="event">
            <h3>Dance Competition</h3>
            <p>Date: 31st January 2025</p>
            <p>Time: 4:00 PM - 7:00 PM</p>
            <p>Venue: Open Stage</p>
            <button class="btn1">Register Now</button>
        </div>
        <div class="event">
            <h3>Art Exhibition</h3>
            <p>Date: 1st February 2025</p>
            <p>Time: 10:00 AM - 5:00 PM</p>
            <p>Venue: Art Gallery</p>
            <button class="btn1">Register Now</button>
        </div>

    </section>

  );
};

export default HeroSection;
